import { useState, useEffect } from 'react'
import Blog from './components/Blog'
import blogService from './services/blogs'
import loginService from './services/login'
import LoginForm from './components/LoginForm'
import BlogForm from './components/BlogForm'



const Togglable = (props) => {

const [blogFormVisible, setBlogFormVisible] = useState(false)

const hideWhenVisible = { display: blogFormVisible ? 'none' : '' }
const showWhenVisible = { display: blogFormVisible ? '' : 'none' }

const toggleVisibility = () => {
  setBlogFormVisible(!blogFormVisible)
}

return (
  <div>
<div style={hideWhenVisible}>
<button onClick={toggleVisibility}>{props.buttonLabel}</button>
</div>
<div style={showWhenVisible}>
<button onClick={toggleVisibility}>cancel</button>
</div>
</div>
)
}



const App = () => {
  const [blogs, setBlogs] = useState([])
  const [username, setUsername] = useState('')
  const [password, setPassword] = useState('')
  const [user, setUser] = useState(null)
  const [message, setMessage] = useState(null)



  const Button = ({text, onClick}) => {
    return (
      <button type="submit" onClick={onClick}>{text}</button>
      )
  }


  const handleLogin = async (event) => {
    event.preventDefault()

    try {
      const user = await loginService.login({
        username, password,
      })
      window.localStorage.setItem(
        'loggedBlogappUser', JSON.stringify(user)
      ) 
      blogService.setToken(user.token)
      setUser(user)
      setUsername('')
      setPassword('')
    } catch (exception) {
      showMessage('Wrong username or password')
      setUsername('')
      setPassword('')
    }
  }

  const handleLoginChange = ({ target }) => setUsername(target.value)
  const handlePasswordChange = ({ target }) => setPassword(target.value)



  const logout = () => {
    window.localStorage.removeItem('loggedBlogappUser')
    setUser(null)
  }


  const showMessage = message => {
    setTimeout(() => {
      setMessage('')}, 5000)
      setMessage(message)
  }



  const handleBlogDelete = (blogId) => {
    if (window.confirm("Do you really want to delete blog?")) {
    blogService.deleteBlog(blogId).then(response => {
      setBlogs(blogs.filter(blog => blog.id !== blogId))
      showMessage("Blog deleted successfully")
    }).catch(error => showMessage("Only author can delete blog"))
  }
  }


  useEffect(() => {
    blogService.getAll().then(blogs => {
      setBlogs( blogs )
    }


    )  
  }, [blogs])

  useEffect(() => {
    const loggedUserJSON = window.localStorage.getItem('loggedBlogappUser')
    if (loggedUserJSON) {
      const user = JSON.parse(loggedUserJSON)
      setUser(user)
      blogService.setToken(user.token)
    }
  }, [])



  if (user === null) {
    return (
      <div>
        { message }
        <Togglable buttonLabel='login'>
      < LoginForm  handleLogin={handleLogin} username={username} password={password} handleLoginChange={handleLoginChange} handlePasswordChange={handlePasswordChange}/>
      </Togglable>
      </div>
    )
  }

  return (
    <div>
      { message }
      <h2>blogs</h2>
      <p>{user.username} logged in <Button text='logout' onClick={logout}/></p>
      < BlogForm />
      {blogs.map(blog =>
      <>
        <Blog key={blog.id} blog={blog} />{  blog.user && user && user.username === blog.user.username && < Button text="delete" onClick={() => handleBlogDelete(blog.id)} /> }
      </>
      )}
    </div>
  )
}


export default App
