const initialState = {
  good: 0,
  ok: 0,
  bad: 0
}

const counterReducer = (state = initialState, action) => {
  console.log(action)
  switch (action.type) {
    case 'GOOD':
      const goodState = state.good + 1
      return state.map(item => item === state.good ? goodState : item)
    case 'OK':
      state.ok += 1
      return state
    case 'BAD':
      state.bad += 1
      return state 
    case 'ZERO':
      return initialState
    default: return state
  }
  
}

export default counterReducer
