export const setFilter = filter => {
    return {
        type: "FILTER",
        payload: filter
    }
}

const filterReducer = (state, action) => {
    switch(action.type) {
        case "FILTER":
            return state.filter(item => item.includes(action.payload))
        default:
            return state 
    }
}

export default filterReducer
