import axios from 'axios'
import { useSelector } from 'react-redux';

const anecdotes = useSelector((state) => state.anecdotes);



  const baseUrl = 'http://localhost:3001/anecdotes'


  const getAll = async () => {
    const response = await axios.get(baseUrl)
    return response.data

  }

  const postAnecdote = async (content) => {
    const object = {content, votes: 0}
    const response = await axios.post(baseUrl, object)
    return response.data
  }

  const deleteAnecdote = async (id) => {
    const response = await axios.delete(`${baseUrl}/${id}`)
    return response.data
  }

  const putAnecdote = async (id) => {
    const content = anecdotes.find(anecdote => anecdote.id === id)
    const object = {...content, votes: content.votes + 1}
    const response = await axios.put(`${baseUrl}/${id}`)
    return response.data
  }


  export default { getAll, postAnecdote, deleteAnecdote }
