import { useState } from "react"

const useField = (name) => {
    const [value, setValue] = useState('')

    const onChange = (event, bool) => {
        if (bool) {
            return setValue('')
        }
        setValue(event.target.value)

    }

    const reset = () => {
        setValue('')
    }


    return {
        name, 
        value, 
        onChange,
    }

}




export default useField
