import { useState } from "react"

const useField = (name) => {
    const [value, setValue] = useState('')

    const onChange = (event) => {
        const reset = (bool) => {

        if (bool) {
            return setValue('')
        }
    }
        setValue(event.target.value)

    }




    return {
        name, 
        value, 
        onChange,
    }

}




export default useField
