import axios from 'axios';
import { useState, useEffect } from 'react';

const useCountry = (name) => {
  const baseUrl = 'https://restcountries.com/v2/name';
  const [country, setCountry] = useState(null);

  useEffect(() => {
    axios.get(`${baseUrl}/${name}`).then((response) => {
      console.log(response);
      setCountry(response.data[0]); // Update to setCountry(response.data[0])
    });
  }, [name]);

  console.log(country);
  return country;
};

const Country = ({ country }) => {
  if (!country) {
    return null;
  }

  return (
    <div>
      <h2>{country.name.common}</h2>
      <p>Capital: {country.capital}</p>
      <p>Population: {country.population}</p>
      <img src={country.flags.png} alt="Country Flag" style={{ height: '100px' }} />
    </div>
  );
};

const useField = (type) => {
  const [value, setValue] = useState('');

  const onChange = (event) => {
    setValue(event.target.value);
  };

  const clear = () => {
    setValue('');
  };

  return {
    type,
    value,
    onChange,
    clear,
  };
};

const App = () => {
  const nameInput = useField('text');
  const [name, setName] = useState('');
  const country = useCountry(name);

  const fetch = (e) => {
    e.preventDefault();
    setName(nameInput.value);
  };

  return (
    <div>
      <form onSubmit={fetch}>
        <input {...nameInput} />
        <button>find</button>
      </form>

      {country && <Country country={country} />} {/* Conditionally render Country component */}
    </div>
  );
};

export default App;
