import { useQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries"


const Recommend = () => {
    const result = useQuery(USER_INFO)   

    const [books, setBooks] = useState(null)


    console.log(result)
    const favGenre = result.data.favoriteGenre


    const booksByGenre = useQuery(ALL_BOOKS_BY_GENRE, {
        variables: { favGenre },
        skip: !favGenre,
      })






      useEffect(() => {
        if (booksByGenre && booksByGenre.data && booksByGenre.data.allBookse) {
            const books = booksByGenre.data.allBooks
            setBooks(books)

        }
      }, [booksByGenre]);




    return(
<table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books && books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    )
}


export default Recommend
