import { useQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries"


const Recommend = () => {
    const result = useQuery(USER_INFO)   

    const [favGenre, setFavGenre] = useState(null)

    const booksByGenre = useQuery(ALL_BOOKS_BY_GENRE, {
        variables: { favGenre },
        skip: !favGenre,
      })

      useEffect(() => {
        if (result.data && result.data.favoriteGenre) {
            setFavGenre(result.data.favoriteGenre)

        }
    }, [result.data.favoriteGenre])



    if (result.loading) {
        return (
            <div>Loading...</div>
        )
      }





      if (booksByGenre.loading || result.loading) {
        return (
            <div>Loading...</div>
        )
      }



            const books = booksByGenre.data.allBooks





    return(
<table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books && books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    )
}


export default Recommend
