import { useQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries"


const Recommend = () => {
    const result = useQuery(USER_INFO)   

    const [favGenre, setFavGenre] = useState(null)
    const [books, setBooks] = useState(null)


    const booksByGenre = useQuery(ALL_BOOKS_BY_GENRE, {
        variables: { favGenre },
        skip: !favGenre,
      })




    useEffect(() => {
        if (result.data && result.data.favoriteGenre && booksByGenre) {
          const favGenre = result.data.favoriteGenre
          setFavGenre(favGenre);  
          const books = booksByGenre.data.allBooks
            setBooks(books)    
        }
      }, [result.data, booksByGenre.data.allBooks]);






    return(
<table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    )
}


export default Recommend
