import { useQuery, useLazyQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries"


const Recommend = ({show}) => {
    const user = useQuery(USER_INFO)

    const [favGenre, setFavGenre] = useState(null)
    const [books, setBooks] = useState("")


    const booksByGenre = useLazyQuery(ALL_BOOKS_BY_GENRE, {
        variables: { favGenre },
        skip: !favGenre,
      })

      useEffect(() => {
        if (user.data) {
            setFavGenre(user.data.favoriteGenre)
            console.log(user)

        }
    }, [user.data])

    useEffect(() => {
        if (user.data && booksByGenre.data) {
            const booksToShow = booksByGenre.data.allBooks
            setBooks(booksToShow)
        }
    }, [booksByGenre.data, user])



    if (!show) {
        return null
    } else if (user.loading || booksByGenre.loading) {
        return (
            <div>Loading...</div>
        )
    }






    return(
<table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books && books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    )
}


export default Recommend
