import { useEffect, useState } from "react"
import { ALL_BOOKS, ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries"


const Recommend = () => {
    const result = useQuery(USER_INFO)   

    const [favGenre, setFavgenre] = useState(null)


    const booksByGenre = useQuery(ALL_BOOKS_BY_GENRE, {
        variables: { favGenre },
        skip: !favGenre,
      })


if (result.loading || booksByGenre.loading) {
