import { useQuery } from "@apollo/client";
import { useEffect, useState } from "react";
import { ALL_BOOKS_BY_GENRE, USER_INFO } from "../queries";

const Recommend = () => {
  const result = useQuery(USER_INFO);

  const [favGenre, setFavGenre] = useState(null);
  const [books, setBooks] = useState(null);

  const booksByGenre = useQuery(ALL_BOOKS_BY_GENRE, {
    variables: { favGenre },
    skip: !favGenre,
  });

  useEffect(() => {
    if (result && result.data && result.data.favoriteGenre) {
      const favGenreRes = result.data.favoriteGenre;
      setFavGenre(favGenreRes);
    }
  }, [result.data]);

  useEffect(() => {
    if (booksByGenre && booksByGenre.data && booksByGenre.data.allBooks) {
      const books = booksByGenre.data.allBooks;
      setBooks(books);
    }
  }, [booksByGenre]);

  return (
    <div>
      <h2>Recommendations</h2>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>Author</th>
            <th>Published</th>
          </tr>
          {books && books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Recommend;
