import { useQuery } from "@apollo/client"
import { useEffect, useState } from "react"
import { ALL_BOOKS, ALL_BOOKS_BY_GENRE } from "../queries"



const Books = (props) => {

  const result = useQuery(ALL_BOOKS)   

  const [fetchBooksByGenre, resultByGenre] = useQuery(ALL_BOOKS_BY_GENRE)

  const [allGenres, setAllGenres] = useState({});
  const [books, setBooks] = useState([])



  useEffect(() => {
    if (result.data && result.data.allBooks) {
      const genres = result.data.allBooks.flatMap(book => book.genres);
      setAllGenres([...new Set(genres)]);      
    }
  }, [result.data]);
  console.log(allGenres);

  useEffect(() => {
    if (result.data && result.data.allBooks) {
      setBooks(result.data.allBooks)
    }
  }, [result.data])


  if (result.loading) {
    return (
      <div>Loading...</div>
    )
  }


  if (!props.show) {
    return null
  }


  const fetchBooksByQuery = async (genre) => {
    const booksByGenre = await fetchBooksByGenre({variables: genre})
    console.log(booksByGenre.data.AllBooks)
    setBooks(booksByGenre.data.AllBooks)
  }



  return (
    <div>
      <h2>books</h2>
<p>by <b>genre</b></p>
      <table>
        <tbody>
          <tr>
            <th></th>
            <th>author</th>
            <th>published</th>
          </tr>
          {books.map((a) => (
            <tr key={a.title}>
              <td>{a.title}</td>
              <td>{a.author.name}</td>
              <td>{a.published}</td>
            </tr>
          ))}
        </tbody>
      </table>
      {allGenres.map(genre => (
        <button key={genre} type="submit" onClick={() => fetchBooksByQuery(genre)}>
  {genre}
</button>
      ))}

    </div>
  )
}

export default Books
