import React, { createContext, useEffect, useState } from 'react';
import Diary from './components/diary';
import { DiaryType } from './types';
import diaryService from './services/diaryService';
import DiaryForm from './components/diaryForm';


const DiaryContext = createContext<[DiaryType[], React.Dispatch<React.SetStateAction<DiaryType[]>>]>([[], () => {handleSetDiaries}]);



const App = () => {
  const [diaries, setDiaries] = useState<DiaryType[]>([]);


  const handleSetDiaries: React.Dispatch<React.SetStateAction<DiaryType[]>> = (newDiaries) => {
    setDiaries(newDiaries);
  };

  useEffect(() => {
    diaryService
    .getDiaries()
    .then(response => {
  setDiaries(response)
})
  }, [diaries])

  return (
<div>
<h2>Add new entry</h2>
<DiaryContext.Provider value={[diaries, setDiaries]}>
      <DiaryForm />
      </DiaryContext.Provider>
      <h2>Diary entries</h2>
  {
    diaries.map(diary => (
      <Diary key={diary.id} diary={diary} />
    ))
  }
</div>
  )
}

export const context = DiaryContext
export default App ;
