import { useState } from "react"
import diaryService from "../services/diaryService"
import { NewDiaryType } from "../types"


const DiaryForm = () => {

    const [date, setDate] = useState('')
    const [visibility, setVisibility] = useState('')
    const [weather, setWeather] = useState('')
    const [comment, setComment] = useState('')
    const [notification, setNotification] = useState(null)
    const [visibilityOption, setVisibilityOption] = useState(null)


    const diaryAddHandler = (event: React.SyntheticEvent) => {
        event.preventDefault()
        const newDiary: NewDiaryType = {
            date: date,
            visibility: visibility,
            weather: weather,
            comment: comment
        }
        diaryService
        .postDiary(newDiary)
        .then((response) => {
            console.log(response)
            setNotification(response.data);
          });
        setDate('')
        setVisibility('')
        setWeather('')
        setComment('')
    }

    const handleVisibilityOptionChange = (event: React.SyntheticEvent) => {
        setVisibilityOption(event.target);
      };


return (
    <div>
        {notification}
<form onSubmit={diaryAddHandler} >
   date: <input 
    type="date"
    value={date}
    onChange={(event) => setDate(event.target.value) }
    /><br />
        visibility: 
        <input type="radio" name="great" onChange={handleVisibilityOptionChange} />

        <input 
    type="radio"
    value={visibility}
    onChange={(event) => setVisibility(event.target.value) }
    /><br />
    weather: <input 
    type="text"
    value={weather}
    onChange={(event) => setWeather(event.target.value) }
    /><br />
    comment: <input 
    type="text"
    value={comment}
    onChange={(event) => setComment(event.target.value) }
    /><br />
    <button type="submit">add Diary</button>
</form>
</div>
)
}


export default DiaryForm
