import { useContext, useState } from "react"
import diaryService from "../services/diaryService"
import { DiaryType, NewDiaryType } from "../types"
import { DiaryContext } from "../App"


const DiaryForm = () => {

    const [date, setDate] = useState('')
    const [visibility, setVisibility] = useState('')
    const [weather, setWeather] = useState('')
    const [comment, setComment] = useState('')
    const [diaries, setDiaries] = useContext<DiaryType[]>(DiaryContext);


    const diaryAddHandler = (event: React.SyntheticEvent) => {
        event.preventDefault()
        const newDiary: NewDiaryType = {
            date: date,
            visibility: visibility,
            weather: weather,
            comment: comment
        }
        diaryService
        .postDiary(newDiary)
        setDate('')
        setVisibility('')
        setWeather('')
        setComment('')
    }

return (
<form onSubmit={diaryAddHandler} >
   date: <input 
    type="text"
    value={date}
    onChange={(event) => setDate(event.target.value) }
    /><br />
        visibility: <input 
    type="text"
    value={visibility}
    onChange={(event) => setVisibility(event.target.value) }
    /><br />
    weather: <input 
    type="text"
    value={weather}
    onChange={(event) => setWeather(event.target.value) }
    /><br />
    comment: <input 
    type="text"
    value={comment}
    onChange={(event) => setComment(event.target.value) }
    /><br />
    <button type="submit">add Diary</button>
</form>
)
}


export default DiaryForm
